  },
  stop_button: {},
  button_image: {
    minWidth: 50,
    width: "100%",
    height: "100%",
    resizeMode: "contain",
  },
  elapsed_time: {
    fontSize: 17,
    color: "#000000",
    fontFamily: "Rany-Medium",
  },
});

export default CurrentTrackingBanner;

import React, { useState } from "react";
import {
  TouchableOpacity,
  Text,
  GestureResponderEvent,
  View,
  ViewStyle,
  TextStyle,
  StyleSheet,
} from "react-native";

/**
 * Props for the CustomButton component.
 *
 * @interface CustomButtonProps
 * @property {function} onPress - Function to handle the button press event.
 * @property {string} title - The title text to display on the button.
 * @property {string} [textColor] - Optional color for the button text.
 * @property {string} [buttonColor] - Optional color for the button background.
 * @property {string} [fontFamily] - Optional font family for the button text.
 * @property {number} [textSize] - Optional size for the button text.
 * @property {string} [label] - Optional label for accessibility purposes.
 * @property {boolean} [addFlex] - Optional flag to add flex styling to the button.
 * @property {boolean} [disabled] - Optional flag to disable the button.
 * @property {number} [verticalPadding] - Optional vertical padding for the button.
 * @property {number} [cornerRadius] - Optional corner radius for the button.
 * @property {number} [lineHeight] - Optional line height for the button text.
 */
interface CustomButtonProps {
  onPress: (event: GestureResponderEvent) => void;
  title: string;
  textColor?: string;
  buttonColor?: string;
  fontFamily?: string;
  textSize?: number;
  label?: string;
  addFlex?: boolean;
  disabled?: boolean;
  verticalPadding?: number;
  cornerRadius?: number;
  lineHeight?: number;
}

const CustomButton: React.FC<CustomButtonProps> = ({
  onPress,
  title,
  textColor,
  buttonColor,
  fontFamily,
  textSize,
  label,
  addFlex = false,
  disabled = false,
  verticalPadding = 15, // Default vertical padding
  cornerRadius, // Optional corner radius
  lineHeight, // Optional line height
}) => {
  const [buttonHeight, setButtonHeight] = useState<number>(0);

  const handleLayout = (event: any) => {
    const { height } = event.nativeEvent.layout;
    setButtonHeight(height);
  };

  const dynamicButtonStyle: ViewStyle = {
    backgroundColor: buttonColor || "#fff",
    borderRadius: cornerRadius !== undefined ? cornerRadius : buttonHeight / 3,
    paddingVertical: verticalPadding,
  };

  const dynamicTextStyle: TextStyle = {
    color: textColor || "#334FD7",
    fontFamily: fontFamily || "Rany-Medium",
    fontSize: textSize || 25,
    textAlign: 'center', // Center text horizontally
    textAlignVertical: 'center', // Center text vertically
    lineHeight: lineHeight, // Apply line height if provided
  };

  const labelTextStyle: TextStyle = {
    marginBottom: 5, // Add some spacing between the label and the button
    fontSize: textSize || 20, // Optional: Match label text size with button text size
    fontFamily: fontFamily || "Rany-Medium",
  };

  const containerStyle = addFlex
    ? localStyles.flexContainer
    : localStyles.container;

  return (
    <View style={containerStyle}>
      {label && <Text style={labelTextStyle}>{label}</Text>}
      <TouchableOpacity
        style={[localStyles.button, dynamicButtonStyle]}
        onLayout={handleLayout}
        onPress={disabled ? undefined : onPress}
        disabled={disabled}
      >
        <Text style={[dynamicTextStyle]}>{title}</Text>
      </TouchableOpacity>
    </View>
  );
};

/**
 * Styles for the custom button.
 */
const localStyles = StyleSheet.create({
  container: {
    flexDirection: "row",
  },
  flexContainer: {
    flexDirection: "row",
    flex: 1,
  },
  button: {
    flex: 1,
    paddingHorizontal: 20,
    alignItems: "center",
    justifyContent: "center",
  },
});

export default CustomButton;

import { StyleSheet, Text, View } from "react-native";
import { Student } from "../databaseModels/databaseClasses/Student";
import SearchBarComponent from "./GeneralInputComponent";
import CustomButton from "./CustomButton";
import { useState } from "react";

interface Props {
  studentNumber: string;
  onConfirmation: () => void;
  onCancel: () => void;
}

/**
 * DataDeletionConfirmationPopup component handles the confirmation process for data deletion.
 *
 * This component prompts the user to confirm their request for data deletion by entering their
 * student number. If the entered student number matches the one provided via props, the deletion
 * is confirmed. Otherwise, an error message is displayed to the console.
 *
 * @component
 * @returns {JSX.Element} A functional component that renders a popup for confirming data deletion.
 *
 * @example
 * <DataDeletionConfirmationPopup studentNumber="ST10020300" onConfirmation={handleConfirm} onCancel={handleCancel} />
 *
 * @remarks
 * - Takes the student's number as a prop and compares it with the input entered by the user.
 * - Displays an error if the input doesn't match the student's number.
 * - Executes `onConfirmation` when the student number matches, and `onCancel` when the cancellation is chosen.
 *
 * @function
 * @name DataDeletionConfirmationPopup
 *
 * @hook
 * @name useState
 * @description Manages local state for the confirmation input and error message visibility.
 *
 * @param {string} studentNumber - The student's number that must be confirmed by the user.
 * @param {function} onConfirmation - Callback executed when the student number is confirmed.
 * @param {function} onCancel - Callback executed when the user cancels the confirmation.
 *
 * @state {string} confirmationInput - Stores the user‚Äôs input for confirming the student number.
 * @state {boolean} hasError - Determines if the error message indicating a mismatch in student numbers should be displayed.
 *
 * @throws Will log an error to the console if the student number is null or empty.
 *
 * @callback handleConfirm
 * @description Checks if the input matches the student number and either calls `onConfirmation` or displays an error.
 */
export default function DataDeletionConfirmationPopup({
  studentNumber,
  onConfirmation,
  onCancel,
}: Props) {
  const [confirmationInput, setConfirmationInput] = useState(""); // State of the confirmation input entered by the user
  const [hasError, setHasError] = useState(false); // Visibility of the error message indicating that the student numbers don't match

  // Don't render if student number is null or empty
  if (!studentNumber) {
    console.error(
      "DataDeletionConfirmationPopup: Student number was null or empty"
    );
    return null;
  }

  const handleConfirm = () => {
    if (confirmationInput.toUpperCase() === studentNumber.toUpperCase()) {
      onConfirmation(); // run onConfirmation code
    } else {
      setHasError(true); // show error if failed validation
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.body}>
        <Text style={styles.title}>ARE YOU SURE?</Text>
        <Text style={styles.message}>
          Please type your student number to confirm:
        </Text>
        <Text style={styles.studentNumber}>{studentNumber.toUpperCase()}</Text>
        <SearchBarComponent
          FGColor="#000000"
          placeHolderColor="#808080"
          placeholderText="STXXXXXXXX"
          labelText=""
          onSearchInputChange={setConfirmationInput}
        />

        {hasError ? (
          <Text style={styles.error_text}>Student Numbers do not match</Text>
        ) : null}

        <View style={styles.buttonContainer}>
          <CustomButton
            title="Confirm"
            onPress={handleConfirm}
            textColor="#161616"
            buttonColor="#A4DB51" // Green color
            fontFamily="Rany-Medium"
            textSize={16}
            addFlex={true}
          />
          <CustomButton
            title="Cancel"
            onPress={onCancel}
            textColor="#161616"
            buttonColor="#EC4E4B" // Red color
            fontFamily="Rany-Medium"
            textSize={16}
            addFlex={true}
          />
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
  body: {
    color: "#161616",
    width: "95%",
    backgroundColor: "#F9F9F9",
