import React, { useState, useRef } from 'react';
import { TextInput, StyleSheet, View } from 'react-native';
import SearchIcon from "../assets/icons/searchIcon";
import { useTheme } from '../styles/ThemeContext';
import { lightTheme, darkTheme } from '../styles/themes';

interface Props {
    FGColor: string;
    onSearchInputChange: (searchInput: string) => void;
    placeHolderColor: string;
    onFocus?: () => void;   // Optional onFocus handler
    onBlur?: (searchInput: string) => void;  // Updated onBlur to receive searchInput
}

function SearchBarComponent({ FGColor, onSearchInputChange, placeHolderColor, onFocus, onBlur }: Props) {
    const [searchInput, setSearchInput] = useState('');
    const [isFocused, setIsFocused] = useState(false); // Track focus state
    const [placeholderText, setPlaceholderText] = useState("Search");
    const { isDarkMode } = useTheme();
    const theme = isDarkMode ? darkTheme : lightTheme;

    // Handle input change and update state
    const handleSearchChange = (text: string) => {
        setSearchInput(text);
        onSearchInputChange(text); // Call the parent component's search function
    };

    // Handle onFocus event
    const handleFocus = () => {
        setIsFocused(true);
        setPlaceholderText("Search");
        setSearchInput(placeholderText !== "Search" ? placeholderText : "");
        if (onFocus) onFocus();
    };

    // Handle onBlur event
    const handleBlur = () => {
        setIsFocused(false);
        if (searchInput.trim() !== '') {
            setPlaceholderText(searchInput); // Set placeholder to the search term
            setSearchInput(''); // Clear the input field
        } else {
            setPlaceholderText("Search"); // Keep the placeholder as "Search" if input is empty
        }
        if (onBlur) onBlur(searchInput); // Pass the current searchInput to onBlur
    };

    return (
        <View>
            <View style={[styles.searchBarContainer, { borderColor: isFocused ? "transparent" : "transparent", backgroundColor: theme.componentBackground }]}>
                <SearchIcon size={15} color={theme.componentTextColour} />
                <TextInput
                    style={[styles.input, { color: theme.componentTextColour }]} // Use FGColor for input text color
                    placeholder= {placeholderText} // Updated placeholder text
                    value={searchInput}
                    onChangeText={handleSearchChange}
                    onFocus={handleFocus}  // Use custom focus handler
                    onBlur={handleBlur}    // Use custom blur handler
                    placeholderTextColor={theme.componentTextColour}
                />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    searchBarContainer: {
        backgroundColor: '#EBEBEB',
        borderRadius: 15,
        padding: 0,
        paddingLeft: 15,
        borderWidth: 3,
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
    },
    input: {
        height: 40,
        paddingHorizontal: 10,
        fontSize: 16,
        fontFamily: 'Rany-Medium',
        width: '100%',
    }
});

export default SearchBarComponent;

import { useRef, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  KeyboardAvoidingView,
  Platform,
} from "react-native";
import MapView, { Marker, Region } from "react-native-maps";
import PlacesInput from "react-native-places-input";
import { useTheme } from '../styles/ThemeContext';
import { lightTheme, darkTheme } from '../styles/themes';

interface Props {
  handlePlaceUpdated: (
    place: string,
    coordinate: { latitude: number; longitude: number }
  ) => void;
}

/**
 * SearchLocation component provides a location search and selection interface.
 *
 * This component allows users to search for a location using the Google Places API, display the selected location on a map, and place a marker at the    selected coordinates. It includes an autocomplete input field and a map view.
 *
 * @component
 * @param {Props} props - Props containing a function to handle place updates.
 * @returns {JSX.Element} The rendered SearchLocation component.
 *
 * @example
 * // Usage example:
 * <SearchLocation handlePlaceUpdated={(place) => console.log(place)} />
 *
 * @remarks
 * - Requires a valid Google Maps API key to function.
 * - Updates the map's region and adds a marker to the map based on the selected place.
 * - Includes keyboard handling with `KeyboardAvoidingView` for a smooth user experience on iOS.
 *
 * @function
 * @name SearchLocation
 *
 * @param {function} handlePlaceUpdated - Callback function to handle updates when a place is selected.
 * `place` is in the format: Street Address, Suburb, City, Postal Code, Country
 *
 * @hook
 * @name useState
 * @description Manages the state for the marker's coordinates.
 *
 * @hook
 * @name useRef
 * @description Provides a reference to the MapView component for controlling the map's region.
 *
 * @state {Object | null} markerCoordinate - The coordinates of the marker on the map.
 *
 * @throws Will throw an error if the Google Maps API key is missing.
 */
export default function SearchLocation({ handlePlaceUpdated }: Props) {
  const [markerCoordinate, setMarkerCoordinate] = useState<{
    latitude: number;
    longitude: number;
  } | null>(null);
  const mapRef = useRef<MapView>(null);
  const googleApiKey = process.env.EXPO_PUBLIC_GOOGLE_MAPS_API_KEY;
  const { isDarkMode } = useTheme();
  const theme = isDarkMode ? darkTheme : lightTheme;

  if (!googleApiKey) {
    throw new Error(
      "Missing Google API Key. Please set EXPO_PUBLIC_GOOGLE_MAPS_API_KEY in your .env"
    );
  }

  return (
    <View style={styles.container}>
